import XCTest
@testable import Mechanica

final class ImageUtilsTests: XCTestCase {

  func testBase64EncodedString() {
    let robot = "data:image/png;base64,"

    XCTAssertNotNil(Image(base64Encoded: robot))

    let emoji = "data:image/png;base64,"

    XCTAssertNotNil(Image(base64Encoded: emoji))


    let broken = "data:image/png;base64,"

    XCTAssertNil(Image(base64Encoded: broken))

    XCTAssertNil(Image(base64Encoded: "this is a random string"))

  }

  func testHasAlpha() throws {
    let url =  resources().appendingPathComponent("pic.png")
    let url2 = resources().appendingPathComponent("pic_no_alpha.jpeg")

    let image: Image
    let imageNoAlpha: Image

    #if os(macOS)
    image = NSImage(contentsOf: url)!
    imageNoAlpha = NSImage(contentsOf: url2)!
    #elseif os(iOS) || os(tvOS) || os(watchOS)
    let data = try Data(contentsOf: url)
    let data2 = try Data(contentsOf: url2)
    image = Image(data: data)!
    imageNoAlpha = Image(data: data2)!
    #endif

    XCTAssertTrue(image.hasAlpha)
    XCTAssertFalse(image.isOpaque)

    XCTAssertFalse(imageNoAlpha.hasAlpha)
    XCTAssertTrue(imageNoAlpha.isOpaque)
  }

  func testData() throws {
    let url1 =  resources().appendingPathComponent("pic.png")
    let url2 = resources().appendingPathComponent("pic_no_alpha.jpeg")

    let image1: Image
    let image2: Image

    #if os(macOS)
    image1 = NSImage(contentsOf: url1)!
    image2 = NSImage(contentsOf: url2)!
    #elseif os(iOS) || os(tvOS) || os(watchOS)
    let data = try Data(contentsOf: url1)
    let data2 = try Data(contentsOf: url2)
    image1 = Image(data: data)!
    image2 = Image(data: data2)!
    #endif

    XCTAssertNotNil(image1.data)
    XCTAssertNotNil(image2.data)
  }

  private func resources() -> URL {
    var resources = URL(fileURLWithPath: #file, isDirectory: false).deletingLastPathComponents(2)
    resources.appendPathComponent("Resources")
    return resources
  }

  func testInflate() {
    let url1 =  resources().appendingPathComponent("pic.png")

    let image1: Image

    #if os(macOS)
    image1 = NSImage(contentsOf: url1)!

    #elseif os(iOS) || os(tvOS) || os(watchOS)
    let data = try! Data(contentsOf: url1)
    image1 = Image(data: data)!
    #endif

    XCTAssertFalse(image1.inflated)

    image1.inflate()
    XCTAssertTrue(image1.inflated)

    image1.inflate()
    XCTAssertTrue(image1.inflated)
  }

}
